import "./clearReplit.js";
import express, { type Request, Response, NextFunction } from "express";
import session from "express-session";
import helmet from "helmet";
import rateLimit from "express-rate-limit";
import csrf from "csurf";
import corsMiddleware from "./corsConfig";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { setupSSL } from "./ssl";
import { initializeSecrets, scheduleKeyRotation } from "./secretsManager";
import { initializeRLS, rlsMiddleware } from "./rowLevelSecurity";
import { immutableAuditMiddleware } from "./immutableAuditLog";
// import { incidentDetectionMiddleware } from "./incidentResponse"; // Disabled in development
import { complianceAutomationManager } from "./complianceAutomation";
import { stripReplitScripts } from "./htmlProcessor";

// Replit variables already cleared by clearReplit.js import

const app = express();

// Trust proxy for accurate IP addresses behind reverse proxies
app.set('trust proxy', 1);

// CORS configuration
app.use(corsMiddleware);

// Security middleware
app.use(helmet({
  contentSecurityPolicy: process.env.NODE_ENV === 'production' ? {
    directives: {
      defaultSrc: ["'self'"],
      styleSrc: ["'self'", "'unsafe-inline'", "https://fonts.googleapis.com"],
      fontSrc: ["'self'", "https://fonts.gstatic.com"],
      imgSrc: ["'self'", "data:", "https:", "blob:"],
      scriptSrc: ["'self'", "'unsafe-eval'"],
      connectSrc: ["'self'", "ws:", "wss:"],
      frameSrc: ["'none'"],
      objectSrc: ["'none'"],
      baseUri: ["'self'"]
    }
  } : false, // Disable CSP in development to allow Vite hot reload
  hsts: {
    maxAge: 31536000,
    includeSubDomains: true,
    preload: true
  }
}));

// Rate limiting
const limiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 1000, // Limit each IP to 1000 requests per windowMs
  message: {
    error: "Too many requests from this IP, please try again later."
  },
  standardHeaders: true,
  legacyHeaders: false,
});

const authLimiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 5, // Limit each IP to 5 auth requests per windowMs
  message: {
    error: "Too many authentication attempts, please try again later."
  },
  standardHeaders: true,
  legacyHeaders: false,
});

app.use(limiter);
app.use('/api/auth', authLimiter);
app.use('/api/login', authLimiter);
app.use('/api/logout', authLimiter);

app.use(express.json({ limit: '10mb' }));
app.use(express.urlencoded({ extended: false, limit: '10mb' }));

// Session middleware for demo mode
app.use(session({
  secret: process.env.SESSION_SECRET || 'demo-session-secret-key',
  resave: false,
  saveUninitialized: false,
  cookie: {
    secure: process.env.NODE_ENV === 'production',
    httpOnly: true,
    maxAge: 24 * 60 * 60 * 1000 // 24 hours
  }
}));

// CSRF Protection - use session-based storage instead of cookies
const csrfProtection = csrf({
  // Use session-based storage instead of cookie-based
  // This is more secure and works better with session middleware
});

// Only enable CSRF in production to prevent development issues
// But exclude demo routes from CSRF protection
if (process.env.NODE_ENV === 'production') {
  app.use((req, res, next) => {
    // Skip CSRF for demo authentication routes
    if (req.path === '/api/demo/login' || req.path === '/api/demo/logout') {
      return next();
    }
    return csrfProtection(req, res, next);
  });
}

// Health check endpoint for deployment
app.get('/health', (_req, res) => {
  res.status(200).json({ 
    status: 'healthy', 
    timestamp: new Date().toISOString(),
    uptime: process.uptime(),
    environment: process.env.NODE_ENV || 'development'
  });
});

// CSRF token endpoint
app.get('/api/csrf-token', (req: any, res) => {
  if (process.env.NODE_ENV === 'production') {
    res.json({ csrfToken: req.csrfToken() });
  } else {
    res.json({ csrfToken: 'dev-token' });
  }
});

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

// Initialize advanced security features
async function initializeAdvancedSecurity() {
  try {
    log('ğŸ” Initializing advanced security features...');

    // Initialize secrets management
    await initializeSecrets();
    scheduleKeyRotation();

    // Initialize Row-Level Security (skip in development to avoid DB schema issues)
    if (process.env.NODE_ENV === 'production') {
      await initializeRLS();
    }

    // Start compliance automation
    complianceAutomationManager.scheduleAutomatedCollection();

    log('âœ… Advanced security initialization completed');
  } catch (error) {
    log('âŒ Advanced security initialization failed:', String(error));
    // Don't crash the server, but log the error
  }
}

// Add advanced security middleware
app.use(rlsMiddleware()); // Row-Level Security context
app.use(immutableAuditMiddleware()); // Immutable audit logging
// Disable incident detection in development to prevent authentication blocking
if (process.env.NODE_ENV === 'production') {
  const { incidentDetectionMiddleware } = require('./incidentResponse');
  app.use(incidentDetectionMiddleware()); // Security incident detection
}

(async () => {
  // Initialize advanced security features
  await initializeAdvancedSecurity();

  const server = await registerRoutes(app);

  // Setup SSL/TLS configuration after routes are registered
  const { app: secureApp, ssl } = setupSSL(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // Add HTML post-processing middleware to strip Replit scripts AFTER Vite setup
  app.use((req, res, next) => {
    if (req.path === '/' || req.accepts('html')) {
      const originalSend = res.send;
      const originalEnd = res.end;

      res.send = function(body) {
        console.log('Response send() called with body type:', typeof body, 'length:', body?.length);
        if (typeof body === 'string') {
          if (body.includes('HIGHLIGHT_COLOR') || body.includes('replit') || body.includes('<html>')) {
            console.log('ğŸ§¹ Stripping Replit content from HTML response');
            body = stripReplitScripts(body);
          }
        }
        return originalSend.call(this, body);
      };

      res.end = function(chunk, encoding) {
        console.log('Response end() called with chunk type:', typeof chunk, 'length:', chunk?.length);
        if (typeof chunk === 'string') {
          if (chunk.includes('HIGHLIGHT_COLOR') || chunk.includes('replit') || chunk.includes('<html>')) {
            console.log('ğŸ§¹ Stripping Replit content from HTML chunk');
            chunk = stripReplitScripts(chunk);
          }
        }
        return originalEnd.call(this, chunk, encoding);
      };
    }

    next();
  });

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  // Handle SSL and non-SSL server startup
  if (ssl) {
    // Greenlock handles its own port management for SSL
    log(`SSL/HTTPS server configured`);
  } else {
    server.listen({
      port,
      host: "0.0.0.0",
      reusePort: true,
    }, () => {
      log(`serving on port ${port}`);
    });
  }
})();

