/**
 * Monitoring & Alerting Service
 * Integration with ELK/Datadog/Prometheus/Sentry for comprehensive monitoring
 */

import { Request, Response } from 'express';
import { securityLogger } from './security';

// Mock Sentry integration (install @sentry/node for real implementation)
interface SentryLike {
  captureException(error: Error, context?: any): void;
  captureMessage(message: string, level?: string): void;
  setUser(user: { id?: string; email?: string }): void;
  setContext(key: string, context: any): void;
}

class MockSentry implements SentryLike {
  captureException(error: Error, context?: any) {
    securityLogger.error('Sentry: Exception captured', {
      error: error.message,
      stack: error.stack,
      context
    });
  }

  captureMessage(message: string, level?: string) {
    securityLogger.info('Sentry: Message captured', { message, level });
  }

  setUser(user: { id?: string; email?: string }) {
    // Context setting for error tracking
  }

  setContext(key: string, context: any) {
    // Context setting for error tracking
  }
}

export const sentry = new MockSentry();

/**
 * Prometheus-style metrics collection
 */
interface MetricData {
  name: string;
  value: number;
  labels?: Record<string, string>;
  timestamp: number;
}

class MetricsCollector {
  private metrics: MetricData[] = [];

  counter(name: string, labels?: Record<string, string>) {
    this.increment(name, 1, labels);
  }

  increment(name: string, value: number = 1, labels?: Record<string, string>) {
    const metric: MetricData = {
      name,
      value,
      labels,
      timestamp: Date.now()
    };

    this.metrics.push(metric);

    // Log for external collection
    securityLogger.info('Metrics: Counter incremented', {
      metric: name,
      value,
      labels
    });
  }

  gauge(name: string, value: number, labels?: Record<string, string>) {
    const metric: MetricData = {
      name,
      value,
      labels,
      timestamp: Date.now()
    };

    this.metrics.push(metric);

    securityLogger.info('Metrics: Gauge set', {
      metric: name,
      value,
      labels
    });
  }

  histogram(name: string, value: number, labels?: Record<string, string>) {
    const metric: MetricData = {
      name: `${name}_duration`,
      value,
      labels,
      timestamp: Date.now()
    };

    this.metrics.push(metric);

    securityLogger.info('Metrics: Histogram recorded', {
      metric: name,
      duration: value,
      labels
    });
  }

  getMetrics(): MetricData[] {
    return this.metrics.slice();
  }

  clearMetrics() {
    this.metrics = [];
  }
}

export const metrics = new MetricsCollector();

/**
 * Performance monitoring middleware
 */
export function performanceMonitoring() {
  return (req: Request, res: Response, next: any) => {
    const startTime = Date.now();
    const originalEnd = res.end;

    res.end = function(this: Response, ...args: any[]) {
      const duration = Date.now() - startTime;

      // Record request metrics
      metrics.counter('http_requests_total', {
        method: req.method,
        route: req.path,
        status_code: res.statusCode.toString()
      });

      metrics.histogram('http_request_duration_ms', duration, {
        method: req.method,
        route: req.path
      });

      // Alert on slow requests
      if (duration > 5000) { // 5 seconds
        sentry.captureMessage(`Slow request detected: ${req.method} ${req.path}`, 'warning');
        securityLogger.warn('Performance: Slow request detected', {
          method: req.method,
          path: req.path,
          duration,
          userAgent: req.get('User-Agent'),
          ip: req.ip
        });
      }

      // Alert on server errors
      if (res.statusCode >= 500) {
        sentry.captureMessage(`Server error: ${req.method} ${req.path} - ${res.statusCode}`, 'error');
        securityLogger.error('Performance: Server error', {
          method: req.method,
          path: req.path,
          statusCode: res.statusCode
        });
      }

      return originalEnd.apply(this, args);
    };

    next();
  };
}

/**
 * Error tracking middleware
 */
export function errorTracking() {
  return (error: Error, req: Request, res: Response, next: any) => {
    // Capture error in Sentry with context
    sentry.setContext('request', {
      method: req.method,
      url: req.url,
      headers: req.headers,
      ip: req.ip,
      userAgent: req.get('User-Agent')
    });

    if ((req as any).user?.claims) {
      sentry.setUser({
        id: (req as any).user.claims.sub,
        email: (req as any).user.claims.email
      });
    }

    sentry.captureException(error);

    // Record error metrics
    metrics.counter('errors_total', {
      type: error.name,
      route: req.path,
      method: req.method
    });

    securityLogger.error('Application error captured', {
      error: error.message,
      stack: error.stack,
      path: req.path,
      method: req.method,
      userId: (req as any).user?.claims?.sub
    });

    next(error);
  };
}

/**
 * Health check endpoints for monitoring
 */
export function setupHealthChecks(app: any) {
  // Basic health check
  app.get('/health', (req: Request, res: Response) => {
    const healthStatus = {
      status: 'healthy',
      timestamp: new Date().toISOString(),
      uptime: process.uptime(),
      version: process.env.npm_package_version || '1.0.0',
      environment: process.env.NODE_ENV || 'development'
    };

    res.json(healthStatus);
  });

  // Detailed health check
  app.get('/health/detailed', (req: Request, res: Response) => {
    const memoryUsage = process.memoryUsage();
    const cpuUsage = process.cpuUsage();

    const detailedHealth = {
      status: 'healthy',
      timestamp: new Date().toISOString(),
      uptime: process.uptime(),
      version: process.env.npm_package_version || '1.0.0',
      environment: process.env.NODE_ENV || 'development',
      memory: {
        rss: `${Math.round(memoryUsage.rss / 1024 / 1024)} MB`,
        heapTotal: `${Math.round(memoryUsage.heapTotal / 1024 / 1024)} MB`,
        heapUsed: `${Math.round(memoryUsage.heapUsed / 1024 / 1024)} MB`,
        external: `${Math.round(memoryUsage.external / 1024 / 1024)} MB`
      },
      cpu: {
        user: cpuUsage.user,
        system: cpuUsage.system
      },
      database: {
        status: 'connected' // TODO: Add real database health check
      }
    };

    res.json(detailedHealth);
  });

  // Metrics endpoint for Prometheus scraping
  app.get('/metrics', (req: Request, res: Response) => {
    const collectedMetrics = metrics.getMetrics();

    // Convert to Prometheus format
    let prometheusMetrics = '';
    const metricGroups: Record<string, MetricData[]> = {};

    // Group metrics by name
    collectedMetrics.forEach(metric => {
      if (!metricGroups[metric.name]) {
        metricGroups[metric.name] = [];
      }
      metricGroups[metric.name].push(metric);
    });

    // Format as Prometheus metrics
    Object.entries(metricGroups).forEach(([name, metricList]) => {
      prometheusMetrics += `# TYPE ${name} counter\n`;
      metricList.forEach(metric => {
        const labels = metric.labels ? 
          Object.entries(metric.labels).map(([k, v]) => `${k}="${v}"`).join(',') : '';
        const labelStr = labels ? `{${labels}}` : '';
        prometheusMetrics += `${name}${labelStr} ${metric.value}\n`;
      });
    });

    res.set('Content-Type', 'text/plain');
    res.send(prometheusMetrics);
  });

  securityLogger.info('Health check endpoints configured');
}

/**
 * Security alerts configuration
 */
export interface SecurityAlert {
  level: 'info' | 'warning' | 'critical';
  type: string;
  message: string;
  details: any;
  timestamp: string;
}

class SecurityAlerting {
  private alerts: SecurityAlert[] = [];

  alert(level: SecurityAlert['level'], type: string, message: string, details?: any) {
    const alert: SecurityAlert = {
      level,
      type,
      message,
      details,
      timestamp: new Date().toISOString()
    };

    this.alerts.push(alert);

    // Log alert
    securityLogger[level](`Security Alert: ${type}`, {
      message,
      details
    });

    // Send to external monitoring (Sentry)
    if (level === 'critical') {
      sentry.captureMessage(`CRITICAL SECURITY ALERT: ${message}`, 'error');
    } else if (level === 'warning') {
      sentry.captureMessage(`Security Warning: ${message}`, 'warning');
    }

    // Keep only last 1000 alerts
    if (this.alerts.length > 1000) {
      this.alerts = this.alerts.slice(-1000);
    }
  }

  getRecentAlerts(count: number = 100): SecurityAlert[] {
    return this.alerts.slice(-count);
  }

  getCriticalAlerts(): SecurityAlert[] {
    return this.alerts.filter(alert => alert.level === 'critical');
  }
}

export const securityAlerting = new SecurityAlerting();

/**
 * Business metrics tracking
 */
export const businessMetrics = {
  casePassportCreated: () => metrics.counter('case_passports_created_total'),
  documentUploaded: () => metrics.counter('documents_uploaded_total'),
  qaAlertCreated: (severity: string) => metrics.counter('qa_alerts_created_total', { severity }),
  userLogin: (role: string) => metrics.counter('user_logins_total', { role }),
  auditLogEntry: (action: string) => metrics.counter('audit_log_entries_total', { action }),

  // Performance metrics
  databaseQueryTime: (operation: string, duration: number) => 
    metrics.histogram('database_query_duration_ms', duration, { operation }),

  // Security metrics
  authFailure: (reason: string) => metrics.counter('auth_failures_total', { reason }),
  accessDenied: (resource: string) => metrics.counter('access_denied_total', { resource }),
  csrfAttempt: () => metrics.counter('csrf_attempts_total')
};

server/objectAcl.ts.content:
import { File } from "@google-cloud/storage";

const ACL_POLICY_METADATA_KEY = "custom:aclPolicy";

// The type of the access group.
//
// Can be flexibly defined according to the use case.
//
// Examples:
// - USER_LIST: the users from a list stored in the database;
// - EMAIL_DOMAIN: the users whose email is in a specific domain;
// - GROUP_MEMBER: the users who are members of a specific group;
// - SUBSCRIBER: the users who are subscribers of a specific service / content
//   creator.
export enum ObjectAccessGroupType {}

// The logic user group that can access the object.
export interface ObjectAccessGroup {
  // The type of the access group.
  type: ObjectAccessGroupType;
  // The logic id that is enough to identify the qualified group members.
  //
  // It may have different format for different types. For example:
  // - for USER_LIST, the id could be the user list db entity id, and the
  //   user list db entity could contain a bunch of user ids. User needs
  //   to be a member of the user list to be able to access the object.
  // - for EMAIL_DOMAIN, the id could be the email domain, and the user needs
  //   to have an email with the domain to be able to access the object.
  // - for GROUP_MEMBER, the id could be the group db entity id, and the
  //   group db entity could contain a bunch of user ids. User needs to be
  //   a member of the group to be able to access the object.
  // - for SUBSCRIBER, the id could be the subscriber db entity id, and the
  //   subscriber db entity could contain a bunch of user ids. User needs to
  //   be a subscriber to be able to access the object.
  id: string;
}

export enum ObjectPermission {
  READ = "read",
  WRITE = "write",
}

export interface ObjectAclRule {
  group: ObjectAccessGroup;
  permission: ObjectPermission;
}

// The ACL policy of the object.
// This would be set as part of the object custom metadata:
// - key: "custom:aclPolicy"
// - value: JSON string of the ObjectAclPolicy object.
export interface ObjectAclPolicy {
  owner: string;
  visibility: "public" | "private";
  aclRules?: Array<ObjectAclRule>;
}

// Check if the requested permission is allowed based on the granted permission.
function isPermissionAllowed(
  requested: ObjectPermission,
  granted: ObjectPermission,
): boolean {
  // Users granted with read or write permissions can read the object.
  if (requested === ObjectPermission.READ) {
    return [ObjectPermission.READ, ObjectPermission.WRITE].includes(granted);
  }

  // Only users granted with write permissions can write the object.
  return granted === ObjectPermission.WRITE;
}

// The base class for all access groups.
//
// Different types of access groups can be implemented according to the use case.
abstract class BaseObjectAccessGroup implements ObjectAccessGroup {
  constructor(
    public readonly type: ObjectAccessGroupType,
    public readonly id: string,
  ) {}

  // Check if the user is a member of the group.
  public abstract hasMember(userId: string): Promise<boolean>;
}

function createObjectAccessGroup(
  group: ObjectAccessGroup,
): BaseObjectAccessGroup {
  switch (group.type) {
    // Implement the case for each type of access group to instantiate.
    //
    // For example:
    // case "USER_LIST":
    //   return new UserListAccessGroup(group.id);
    // case "EMAIL_DOMAIN":
    //   return new EmailDomainAccessGroup(group.id);
    // case "GROUP_MEMBER":
    //   return new GroupMemberAccessGroup(group.id);
    // case "SUBSCRIBER":
    //   return new SubscriberAccessGroup(group.id);
    default:
      throw new Error(`Unknown access group type: ${group.type}`);
  }
}

// Sets the ACL policy to the object metadata.
export async function setObjectAclPolicy(
  objectFile: File,
  aclPolicy: ObjectAclPolicy,
): Promise<void> {
  const [exists] = await objectFile.exists();
  if (!exists) {
    throw new Error(`Object not found: ${objectFile.name}`);
  }

  await objectFile.setMetadata({
    metadata: {
      [ACL_POLICY_METADATA_KEY]: JSON.stringify(aclPolicy),
    },
  });
}

// Gets the ACL policy from the object metadata.
export async function getObjectAclPolicy(
  objectFile: File,
): Promise<ObjectAclPolicy | null> {
  const [metadata] = await objectFile.getMetadata();
  const aclPolicy = metadata?.metadata?.[ACL_POLICY_METADATA_KEY];
  if (!aclPolicy) {
    return null;
  }
  return JSON.parse(aclPolicy as string);
}

// Checks if the user can access the object.
export async function canAccessObject({
  userId,
  objectFile,
  requestedPermission,
}: {
  userId?: string;
  objectFile: File;
  requestedPermission: ObjectPermission;
}): Promise<boolean> {
  // When this function is called, the acl policy is required.
  const aclPolicy = await getObjectAclPolicy(objectFile);
  if (!aclPolicy) {
    return false;
  }

  // Public objects are always accessible for read.
  if (
    aclPolicy.visibility === "public" &&
    requestedPermission === ObjectPermission.READ
  ) {
    return true;
  }

  // Access control requires the user id.
  if (!userId) {
    return false;
  }

  // The owner of the object can always access it.
  if (aclPolicy.owner === userId) {
    return true;
  }

  // Go through the ACL rules to check if the user has the required permission.
  for (const rule of aclPolicy.aclRules || []) {
    const accessGroup = createObjectAccessGroup(rule.group);
    if (
      (await accessGroup.hasMember(userId)) &&
      isPermissionAllowed(requestedPermission, rule.permission)
    ) {
      return true;
    }
  }

  return false;
}

server/objectStorage.ts.content:
import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";

// Simple ACL types for medical documents
export enum ObjectPermission {
  READ = "read",
  WRITE = "write",
}

export interface ObjectAclPolicy {
  owner: string;
  visibility: "public" | "private";
}

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

// The object storage client is used to interact with the object storage service.
export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

// The object storage service is used to interact with the object storage service.
export class ObjectStorageService {
  constructor() {}

  // Gets the public object search paths.
  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  // Gets the private object directory.
  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  // Search for a public object from the search paths.
  async searchPublicObject(filePath: string): Promise<File | null> {
    for (const searchPath of this.getPublicObjectSearchPaths()) {
      const fullPath = `${searchPath}/${filePath}`;

      // Full path format: /<bucket_name>/<object_name>
      const { bucketName, objectName } = parseObjectPath(fullPath);
      const bucket = objectStorageClient.bucket(bucketName);
      const file = bucket.file(objectName);

      // Check if file exists
      const [exists] = await file.exists();
      if (exists) {
        return file;
      }
    }

    return null;
  }

  // Downloads an object to the response.
  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      // Get file metadata
      const [metadata] = await file.getMetadata();
      // For now, treat all medical documents as private
      const isPublic = false;
      // Set appropriate headers
      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `${
          isPublic ? "public" : "private"
        }, max-age=${cacheTtlSec}`,
      });

      // Stream the file to the response
      const stream = file.createReadStream();

      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }

  // Gets the upload URL for an object entity.
  async getObjectEntityUploadURL(): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    if (!privateObjectDir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }

    const objectId = randomUUID();
    const fullPath = `${privateObjectDir}/uploads/${objectId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the object entity file from the object path.
  async getObjectEntityFile(objectPath: string): Promise<File> {
    if (!objectPath.startsWith("/objects/")) {
      throw new ObjectNotFoundError();
    }

    const parts = objectPath.slice(1).split("/");
    if (parts.length < 2) {
      throw new ObjectNotFoundError();
    }

    const entityId = parts.slice(1).join("/");
    let entityDir = this.getPrivateObjectDir();
    if (!entityDir.endsWith("/")) {
      entityDir = `${entityDir}/`;
    }
    const objectEntityPath = `${entityDir}${entityId}`;
    const { bucketName, objectName } = parseObjectPath(objectEntityPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const objectFile = bucket.file(objectName);
    const [exists] = await objectFile.exists();
    if (!exists) {
      throw new ObjectNotFoundError();
    }
    return objectFile;
  }

  normalizeObjectEntityPath(rawPath: string): string {
    if (!rawPath.startsWith("https://storage.googleapis.com/")) {
      return rawPath;
    }

    // Extract the path from the URL by removing query parameters and domain
    const url = new URL(rawPath);
    const rawObjectPath = url.pathname;

    let objectEntityDir = this.getPrivateObjectDir();
    if (!objectEntityDir.endsWith("/")) {
      objectEntityDir = `${objectEntityDir}/`;
    }

    if (!rawObjectPath.startsWith(objectEntityDir)) {
      return rawObjectPath;
    }

    // Extract the entity ID from the path
    const entityId = rawObjectPath.slice(objectEntityDir.length);
    return `/objects/${entityId}`;
  }

  // Simplified access control - in a full implementation, this would check ACL policies
  async canAccessObjectEntity({
    userId,
    objectFile,
  }: {
    userId?: string;
    objectFile: File;
    requestedPermission?: ObjectPermission;
  }): Promise<boolean> {
    // For medical documents, require authentication
    return !!userId;
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}

server/rbacMiddleware.ts.content:
import { Request, Response, NextFunction } from 'express';
import { UserRole, Permission, checkPermission } from '@shared/rbac';
import { securityLogger } from './security';

// Extended request interface with user context
interface AuthenticatedRequest extends Request {
  user?: {
    claims?: {
      sub: string;
      email?: string;
    };
    role?: UserRole;
    permissions?: Permission[];
  };
}

// RBAC Middleware Factory
export function requirePermission(permission: Permission) {
  return async (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    try {
      // Check if user is authenticated
      if (!req.user?.claims?.sub) {
        securityLogger.warn('RBAC: Unauthenticated access attempt', {
          path: req.path,
          method: req.method,
          ip: req.ip,
          permission: permission
        });
        return res.status(401).json({ 
          error: 'Authentication required',
          code: 'UNAUTHORIZED' 
        });
      }

      // Get user role (default to patient if not set)
      const userRole = req.user.role || UserRole.PATIENT;

      // Check if user has the required permission
      if (!checkPermission(userRole, permission)) {
        securityLogger.warn('RBAC: Insufficient permissions', {
          userId: req.user.claims.sub,
          userEmail: req.user.claims.email,
          userRole,
          requiredPermission: permission,
          path: req.path,
          method: req.method,
          ip: req.ip
        });

        return res.status(403).json({ 
          error: 'Insufficient permissions',
          code: 'FORBIDDEN',
          required: permission,
          userRole 
        });
      }

      // Log successful authorization
      securityLogger.info('RBAC: Permission granted', {
        userId: req.user.claims.sub,
        userRole,
        permission,
        path: req.path,
        method: req.method
      });

      next();
    } catch (error) {
      securityLogger.error('RBAC: Authorization check failed', {
        error: error instanceof Error ? error.message : 'Unknown error',
        path: req.path,
        method: req.method,
        ip: req.ip
      });

      return res.status(500).json({ 
        error: 'Authorization check failed',
        code: 'INTERNAL_ERROR' 
      });
    }
  };
}

// Multi-role permission check (user needs ANY of the specified roles)
export function requireAnyRole(...roles: UserRole[]) {
  return async (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    try {
      if (!req.user?.claims?.sub) {
        return res.status(401).json({ 
          error: 'Authentication required',
          code: 'UNAUTHORIZED' 
        });
      }

      const userRole = req.user.role || UserRole.PATIENT;

      if (!roles.includes(userRole)) {
        securityLogger.warn('RBAC: Role access denied', {
          userId: req.user.claims.sub,
          userRole,
          requiredRoles: roles,
          path: req.path
        });

        return res.status(403).json({ 
          error: 'Role access denied',
          code: 'FORBIDDEN',
          requiredRoles: roles,
          userRole 
        });
      }

      next();
    } catch (error) {
      securityLogger.error('RBAC: Role check failed', {
        error: error instanceof Error ? error.message : 'Unknown error',
        path: req.path
      });

      return res.status(500).json({ 
        error: 'Role check failed',
        code: 'INTERNAL_ERROR' 
      });
    }
  };
}

// Admin-only access
export const requireAdmin = requireAnyRole(UserRole.ADMIN);

// Medical professional access (Admin or Doctor)
export const requireMedicalAccess = requireAnyRole(UserRole.ADMIN, UserRole.DOCTOR);

// Data access logging with RBAC context
export function logDataAccessWithRBAC(operation: 'read' | 'write' | 'delete') {
  return (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
    const userId = req.user?.claims?.sub || 'anonymous';
    const userRole = req.user?.role || 'unknown';

    securityLogger.info('Data Access with RBAC', {
      userId,
      userRole,
      operation,
      resource: req.path,
      method: req.method,
      ip: req.ip,
      userAgent: req.get('User-Agent')
    });

    next();
  };
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

// Only require REPLIT_DOMAINS in production mode
if (process.env.NODE_ENV === 'production' && !process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: true,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    name: 'aorta-mesh-session',
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production',
      sameSite: 'strict',
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  // Setup session management for both dev and production
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  // Skip OIDC setup in development mode but keep session management
  if (process.env.NODE_ENV !== 'production') {
    console.log("Using session-based auth in development mode");

    // Simple session serialization for dev mode
    passport.serializeUser((user: Express.User, cb) => cb(null, user));
    passport.deserializeUser((user: Express.User, cb) => cb(null, user));
    return;
  }

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  // Development mode: check for demo session
  if (process.env.NODE_ENV !== 'production') {
    const demoUser = (req.session as any)?.demoUser;
    if (demoUser) {
      (req as any).user = {
        claims: {
          sub: demoUser.id,
          email: demoUser.email,
          first_name: demoUser.firstName,
          last_name: demoUser.lastName,
          profile_image_url: demoUser.profileImageUrl
        }
      };
      return next();
    }
    return res.status(401).json({ message: "Unauthorized" });
  }

  // Production mode: check real authentication
  const user = req.user as any;

  if (!req.isAuthenticated() || !user?.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express, Request, Response, NextFunction } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { auditLogger, logDataAccess, logPhiAccess } from "./security";
import { PHIEncryption } from "./encryptionService";
import { Permission, UserRole, hasPermission } from "@shared/rbac";
import { 
  insertCasePassportSchema, 
  insertDonorSchema, 
  insertDocumentSchema,
  insertQaAlertSchema,
  insertActivityLogSchema,
  insertChainOfCustodySchema 
} from "@shared/schema";
import {
  ObjectStorageService,
  ObjectNotFoundError,
  ObjectPermission,
} from "./objectStorage";

// Extended Request type with user session
interface AuthRequest extends Request {
  user?: any;
}

// Permission middleware factory
function requirePermission(permission: Permission) {
  return async (req: Request, res: Response, next: NextFunction) => {
    const authReq = req as AuthRequest;
    const userId = authReq.user?.claims?.sub;

    if (!userId) {
      return res.status(401).json({ message: "Unauthorized" });
    }

    const user = await storage.getUser(userId);
    if (!user || !user.role) {
      return res.status(403).json({ message: "Forbidden: No role assigned" });
    }

    if (!hasPermission(user.role as UserRole, permission)) {
      logPhiAccess(req, 'permission_check', permission, 'deny', false);
      return res.status(403).json({ message: "Forbidden: Insufficient permissions" });
    }

    next();
  };
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup production authentication
  await setupAuth(app);

  // Production authentication endpoints are handled by setupAuth
  // These include /api/login, /api/callback, /api/logout

  // Auth routes - production authentication
  app.get('/api/auth/user', isAuthenticated, auditLogger('user_profile_access'), async (req: AuthRequest, res) => {
    try {
      logDataAccess(req as Request, 'user_profile', 'read');
      logPhiAccess(req as Request, 'user', req.user?.claims?.sub, 'view');

      const userId = req.user?.claims?.sub;
      if (!userId) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const user = await storage.getUser(userId);
      if (!user) {
        // Create user if doesn't exist
        const newUser = await storage.upsertUser({
          id: userId,
          email: req.user?.claims?.email,
          firstName: req.user?.claims?.first_name,
          lastName: req.user?.claims?.last_name,
          profileImageUrl: req.user?.claims?.profile_image_url,
          role: 'coordinator',
          organizationId: 'org-1' // Default org, should be set during onboarding
        });
        return res.json(newUser);
      }

      // Decrypt sensitive fields if user has permission
      if (user.phoneNumber) {
        user.phoneNumber = PHIEncryption.decryptPhone(user.phoneNumber);
      }

      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Development-only demo login (disabled in production)
  if (process.env.NODE_ENV === 'development') {
    app.head('/api/demo/login', (req, res) => {
      res.status(200).end();
    });

    app.post('/api/demo/login', auditLogger('demo_login'), async (req: AuthRequest, res) => {
      try {
        // Development demo user for testing
        const demoUser = {
          id: 'demo-user-dev',
          email: 'dev@aortamesh.com',
          firstName: 'Development',
          lastName: 'User',
          role: 'admin', // Admin for full access in dev
          organizationId: 'org-1',
          profileImageUrl: null,
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString()
        };

        req.session.demoUser = demoUser;
        res.json({ success: true, user: demoUser });
      } catch (error) {
        console.error("Demo login error:", error);
        res.status(500).json({ message: "Demo login failed" });
      }
    });

    app.post('/api/demo/logout', auditLogger('demo_logout'), async (req: AuthRequest, res) => {
      try {
        req.session.demoUser = null;
        res.json({ success: true });
      } catch (error) {
        console.error("Demo logout error:", error);
        res.status(500).json({ message: "Demo logout failed" });
      }
    });
  }

  // Dashboard statistics - requires authentication
  app.get('/api/dashboard/stats', isAuthenticated, auditLogger('dashboard_stats_access'), async (req: AuthRequest, res) => {
    try {
      logDataAccess(req as Request, 'dashboard_stats', 'read');

      // Get user's organization from session
      const userId = req.user?.claims?.sub;
      const user = await storage.getUser(userId);
      const organizationId = user?.organizationId || "org-1";

      const stats = await storage.getDashboardStats(organizationId);
      res.json(stats);
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ message: "Failed to fetch dashboard statistics" });
    }
  });

  // Case passport routes - requires authentication and permission
  app.get('/api/case-passports', 
    isAuthenticated,
    requirePermission(Permission.VIEW_CASE_PASSPORTS),
    auditLogger('case_passports_list'),
    async (req: AuthRequest, res) => {
      try {
        logDataAccess(req as Request, 'case_passports', 'list');

        // Get user's organization
        const userId = req.user?.claims?.sub;
        const user = await storage.getUser(userId);
        const organizationId = user?.organizationId || "org-1";

        const casePassports = await storage.getCasePassports(organizationId);
        res.json(casePassports);
      } catch (error) {
        console.error("Error fetching case passports:", error);
        res.status(500).json({ message: "Failed to fetch case passports" });
      }
    }
  );

  app.get('/api/case-passports/:id', async (req, res) => {
    try {
      const { id } = req.params;
      const casePassport = await storage.getCasePassport(id);

      if (!casePassport) {
        return res.status(404).json({ message: "Case passport not found" });
      }

      res.json(casePassport);
    } catch (error) {
      console.error("Error fetching case passport:", error);
      res.status(500).json({ message: "Failed to fetch case passport" });
    }
  });

  app.post('/api/case-passports', async (req: any, res) => {
    try {
      // Demo mode - use session user
      const demoUser = req.session?.demoUser;
      if (!demoUser) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const validatedData = insertCasePassportSchema.parse({
        ...req.body,
        createdById: demoUser.id,
        organizationId: demoUser.organizationId,
      });

      const casePassport = await storage.createCasePassport(validatedData);

      // Log the activity
      await storage.createActivityLog({
        casePassportId: casePassport.id,
        userId: demoUser.id,
        action: 'case_passport_created',
        description: `Case passport ${casePassport.caseNumber} created`,
      });

      res.status(201).json(casePassport);
    } catch (error) {
      console.error("Error creating case passport:", error);
      res.status(500).json({ message: "Failed to create case passport" });
    }
  });

  // Donor routes
  app.get('/api/case-passports/:id/donor', async (req, res) => {
    try {
      const { id } = req.params;
      const donor = await storage.getDonorByCasePassport(id);
      res.json(donor);
    } catch (error) {
      console.error("Error fetching donor:", error);
      res.status(500).json({ message: "Failed to fetch donor information" });
    }
  });

  app.post('/api/case-passports/:id/donor', async (req: any, res) => {
    try {
      const { id } = req.params;
      // Demo mode - use session user
      const demoUser = req.session?.demoUser;
      if (!demoUser) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const validatedData = insertDonorSchema.parse({
        ...req.body,
        casePassportId: id,
      });

      const donor = await storage.createDonor(validatedData);

      // Log the activity
      await storage.createActivityLog({
        casePassportId: id,
        userId: demoUser.id,
        action: 'donor_information_added',
        description: 'Donor information added to case passport',
      });

      res.status(201).json(donor);
    } catch (error) {
      console.error("Error creating donor:", error);
      res.status(500).json({ message: "Failed to create donor information" });
    }
  });

  // Document routes
  app.get('/api/case-passports/:id/documents', async (req, res) => {
    try {
      const { id } = req.params;
      const documents = await storage.getDocumentsByCasePassport(id);
      res.json(documents);
    } catch (error) {
      console.error("Error fetching documents:", error);
      res.status(500).json({ message: "Failed to fetch documents" });
    }
  });

  app.post('/api/case-passports/:id/documents', async (req: any, res) => {
    try {
      const { id } = req.params;
      // Demo mode - use session user
      const demoUser = req.session?.demoUser;
      if (!demoUser) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const validatedData = insertDocumentSchema.parse({
        ...req.body,
        casePassportId: id,
        uploadedById: demoUser.id,
      });

      const document = await storage.createDocument(validatedData);

      // Log the activity
      await storage.createActivityLog({
        casePassportId: id,
        userId: demoUser.id,
        action: 'document_uploaded',
        description: `Document ${document.fileName} uploaded`,
      });

      res.status(201).json(document);
    } catch (error) {
      console.error("Error creating document:", error);
      res.status(500).json({ message: "Failed to upload document" });
    }
  });

  // QA Alert routes
  app.get('/api/qa-alerts', auditLogger('qa_alerts_access'), async (req: any, res) => {
    try {
      logDataAccess(req, 'qa_alerts', 'read');
      const alerts = await storage.getQaAlerts("org-1");
      res.json(alerts);
    } catch (error) {
      console.error("Error fetching QA alerts:", error);
      res.status(500).json({ message: "Failed to fetch QA alerts" });
    }
  });

  app.post('/api/qa-alerts', async (req: any, res) => {
    try {
      // Demo mode - use session user
      const demoUser = req.session?.demoUser;
      if (!demoUser) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const validatedData = insertQaAlertSchema.parse({
        ...req.body,
        createdById: demoUser.id,
      });

      const alert = await storage.createQaAlert(validatedData);

      // Log the activity
      await storage.createActivityLog({
        casePassportId: alert.casePassportId,
        userId: demoUser.id,
        action: 'qa_alert_created',
        description: `QA alert created: ${alert.title}`,
      });

      res.status(201).json(alert);
    } catch (error) {
      console.error("Error creating QA alert:", error);
      res.status(500).json({ message: "Failed to create QA alert" });
    }
  });

  // Activity logs
  app.get('/api/activity-logs', auditLogger('activity_logs_access'), async (req, res) => {
    try {
      logDataAccess(req, 'activity_logs', 'read');
      const { casePassportId, limit } = req.query;
      const logs = await storage.getActivityLogs(
        casePassportId as string, 
        limit ? parseInt(limit as string) : 50
      );
      res.json(logs);
    } catch (error) {
      console.error("Error fetching activity logs:", error);
      res.status(500).json({ message: "Failed to fetch activity logs" });
    }
  });

  // Chain of custody routes
  app.get('/api/case-passports/:id/chain-of-custody', async (req, res) => {
    try {
      const { id } = req.params;
      const chain = await storage.getChainOfCustody(id);
      res.json(chain);
    } catch (error) {
      console.error("Error fetching chain of custody:", error);
      res.status(500).json({ message: "Failed to fetch chain of custody" });
    }
  });

  app.post('/api/case-passports/:id/chain-of-custody', async (req: any, res) => {
    try {
      const { id } = req.params;
      // Demo mode - use session user
      const demoUser = req.session?.demoUser;
      if (!demoUser) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const validatedData = insertChainOfCustodySchema.parse({
        ...req.body,
        casePassportId: id,
        createdById: demoUser.id,
      });

      const entry = await storage.createChainOfCustodyEntry(validatedData);

      // Log the activity
      await storage.createActivityLog({
        casePassportId: id,
        userId: demoUser.id,
        action: 'chain_of_custody_updated',
        description: `Chain of custody entry added: ${entry.eventType}`,
      });

      res.status(201).json(entry);
    } catch (error) {
      console.error("Error creating chain of custody entry:", error);
      res.status(500).json({ message: "Failed to create chain of custody entry" });
    }
  });

  // Object storage routes for document management
  app.get("/objects/:objectPath(*)", async (req: any, res) => {
    // Demo mode - use session user
    const demoUser = req.session?.demoUser;
    const userId = demoUser?.id;
    const objectStorageService = new ObjectStorageService();
    try {
      const objectFile = await objectStorageService.getObjectEntityFile(
        req.path,
      );
      const canAccess = await objectStorageService.canAccessObjectEntity({
        objectFile,
        userId: userId,
        requestedPermission: ObjectPermission.READ,
      });
      if (!canAccess) {
        return res.sendStatus(401);
      }
      objectStorageService.downloadObject(objectFile, res);
    } catch (error) {
      console.error("Error checking object access:", error);
      if (error instanceof ObjectNotFoundError) {
        return res.sendStatus(404);
      }
      return res.sendStatus(500);
    }
  });

  app.post("/api/objects/upload", async (req, res) => {
    const objectStorageService = new ObjectStorageService();
    try {
      const uploadURL = await objectStorageService.getObjectEntityUploadURL();
      res.json({ uploadURL });
    } catch (error) {
      console.error("Error generating upload URL:", error);
      res.status(500).json({ error: "Failed to generate upload URL" });
    }
  });

  app.put("/api/documents/:id/file", async (req: any, res) => {
    if (!req.body.fileURL) {
      return res.status(400).json({ error: "fileURL is required" });
    }

    // Demo mode - use session user
    const demoUser = req.session?.demoUser;
    if (!demoUser) {
      return res.status(401).json({ error: "Unauthorized" });
    }
    const { id } = req.params;

    try {
      const objectStorageService = new ObjectStorageService();
      const objectPath = objectStorageService.normalizeObjectEntityPath(req.body.fileURL);

      // Update document with the file path
      const document = await storage.updateDocumentStatus(id, "pending", demoUser.id);

      // Log the activity
      await storage.createActivityLog({
        casePassportId: document.casePassportId,
        userId: demoUser.id,
        action: 'document_uploaded',
        description: `Document file uploaded for ${document.fileName}`,
      });

      res.status(200).json({
        objectPath: objectPath,
        document,
      });
    } catch (error) {
      console.error("Error setting document file:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

